//       navigationOptions: {
//         title: 'Review Details',
//         headerTintColor:'red',
//         headerStyle: {backgroundColor: '#A7CFCC'}
//     }
//   },
};

// home stack navigator screens
const HomeStack = createStackNavigator(screens);

export default HomeStack; //<--this puts the HomeStack data in one conponents
