import { StatusBar } from 'expo-status-bar';
import { StyleSheet, ImageBackground, Text, View, Modal, TouchableOpacity, TouchableWithoutFeedback, Keyboard, Form, Button } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';// form icon
import { AntDesign } from '@expo/vector-icons'; //user icon
import { Ionicons } from '@expo/vector-icons';  //i icon
import React, { useState } from 'react';
import { ScrollView } from 'react-native-gesture-handler';
import Login from './login';
import MakeAppt from './makeAppt1';


export default function Home({ navigation }) {

  const [loginModalOpen, setLoginModalOpen] = useState(false);
  const [apptModelOpen, setApptModelOpen] = useState(false);

  const [pageNum, setPageNum] = useState(0);

  const addPageNum = () => {
    var sum
    sum = pageNum + 1;
    setPageNum(sum);
    console.log("sum:"+sum);
    console.log("pageNum:"+ pageNum)
    if(LoginModalOpen==false){
      setPageNum(0)
      sum=0
      console.log("it worked Jerry")
    }
  }

  // const closeModal = () => {
  //   setLoginModalOpen(false)
  //   setPageNum(0)
  // }

  return (
    <>
      {/* start of pop up models */}
      <Modal visible={loginModalOpen} animationType='slide'>
        <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
          <View style={StyleSheet.modelCon}>
            <MaterialIcons
              name='close'
              size={27}
              style={{ ...styles.openClose, ...styles.openClose2 }} //how to add two styles
              onPress={() => {setLoginModalOpen(false)}}
            />
            <Login />
          </View>
        </TouchableWithoutFeedback>
      </Modal>

      <Modal visible={apptModelOpen} animationType='slide'>
        <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
          <View style={StyleSheet.modelCon}>
            <MaterialIcons
              name='close'
              size={27}
              style={{ ...styles.openClose, ...styles.openClose2 }} //how to add two styles
              onPress={() => setApptModelOpen(false)}
            />
            {/* <MakeAppt/> */}
            
            <Text>{pageNum}</Text>
          
            <Button title='Next' onPress={addPageNum}></Button>
          </View>
        </TouchableWithoutFeedback>
      </Modal>
      {/* end of pop up models */}


      <View style={styles.body}>

        <ScrollView>

          <View style={styles.boxCon}>

            <TouchableOpacity onPress={() => setLoginModalOpen(true)}>
              <View style={styles.box}>
                <AntDesign name="user" style={styles.boxIcon} size={35} color="black" />
                <Text style={styles.boxText}>Sign In</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => setApptModelOpen(true)}>
              <View style={styles.box}>
                <Text style={styles.boxText2}>Make</Text>
                <MaterialCommunityIcons name="format-float-none" style={styles.boxIcon2} size={35} color="black" />
                <Text style={styles.boxText2}> Appt.</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('About')}>
              <View style={styles.box}>
                <Ionicons name="ios-information-circle-outline" style={styles.boxIcon} size={35} color="black" />
                <Text style={styles.boxText}>About</Text>
              </View>
            </TouchableOpacity>

          </View>

          {/* <View style={styles.pictureBox}></View> */}
          <ImageBackground style={styles.pictureBox} source={require('../images/santa_clarita.jpg')} />
          <Text style={styles.boldText}>We Work all throughout The Santa Clarita Valley... </Text>
          <Text style={styles.lowText}> Most of the san Fernando Valley and some parts of the los Angeles</Text>

        </ScrollView>

      </View>
    </>
  );
}

const styles = StyleSheet.create({
  body: {
    backgroundColor: '#E6EBF5',
    height: '100%',
  },
  boxCon: {
    flexDirection: 'row',
    alignContent: 'center',
    alignSelf: 'center',
    marginTop: 20,
  },
  box: {
    // backgroundColor: '#3B76F7',
    backgroundColor: '#1AAD8F',
    borderColor: 'black',
    height: 110,
    width: 110,
    margin: 5,
  },
  boxText: {
    alignSelf: 'center',
    color: 'white',
    fontSize: 25,
  },
  boxText2: {
    alignSelf: 'center',
    color: 'white',
    fontSize: 25,
  },
  boxIcon: {
    alignSelf: 'center',
    paddingTop: 30,
  },
  boxIcon2: {
    alignSelf: 'center',
    // paddingTop: 30,
  },
  pictureBox: {
    backgroundColor: 'gray',
    resizeMode: "cover",
    justifyContent: "center",
    alignSelf: 'center',
    marginTop: 20,
    height: 260,
    width: 340,
  },
  boldText: {
    alignSelf: 'center',
    fontWeight: 'bold',
    fontSize: 30,
    marginTop: 10,
  },
  lowText: {
    alignSelf: 'center',
    paddingTop: 5,
    fontSize: 20,
    padding: 2,
  },
  openClose: {
    borderColor: '#f2f2f2',
    alignSelf: 'center',
    marginBottom: 10,
    borderRadius: 10,
    borderWidth: 1,
    marginTop: 15,
    padding: 10,
  },
  openClose2: {
    marginBottom: 0,
    marginTop: 25,
  },
});
